pragma solidity ^0.6.4;

import "@openzeppelin/contracts-ethereum-package/contracts/access/Ownable.sol";

import "../drip/BalanceDripManager.sol";
import "../drip/VolumeDrip.sol";

contract ComptrollerStorage is OwnableUpgradeSafe {
  uint256 internal _reserveRateMantissa;

  uint256 lastVolumeDripId;
  mapping(uint256 => VolumeDrip.State) volumeDrips;
  mapping(uint256 => address) volumeDripTokens;

  struct PrizeStrategyVolumeDripManager {
    mapping(address => uint256[]) activeMeasureVolumeDripIndices;
    mapping(address => uint256[]) activeMeasureReferralVolumeDripIndices;
  }

  mapping(address => PrizeStrategyVolumeDripManager) internal prizeStrategyVolumeDripManagers;
  mapping(address => BalanceDripManager.State) internal balanceDrips;
}

contracts/drip/BalanceDripManager.sol.content:
pragma solidity ^0.6.4;

import "../utils/MappedSinglyLinkedList.sol";
import "./BalanceDrip.sol";

library BalanceDripManager {
  using SafeMath for uint256;
  using MappedSinglyLinkedList for MappedSinglyLinkedList.Mapping;
  using BalanceDrip for BalanceDrip.State;

  struct State {
    mapping(address => MappedSinglyLinkedList.Mapping) activeBalanceDrips;
    mapping(address => mapping(address => BalanceDrip.State)) balanceDrips;
  }

  function updateDrips(
    State storage self,
    address measure,
    address user,
    uint256 measureBalance,
    uint256 measureTotalSupply,
    uint256 currentTime
  ) internal {
    address currentDripToken = self.activeBalanceDrips[measure].addressMap[MappedSinglyLinkedList.SENTINAL];
    while (currentDripToken != address(0) && currentDripToken != MappedSinglyLinkedList.SENTINAL) {
      BalanceDrip.State storage dripState = self.balanceDrips[measure][currentDripToken];
      dripState.drip(
        user,
        measureBalance,
        measureTotalSupply,
        currentTime
      );
      currentDripToken = self.activeBalanceDrips[measure].addressMap[currentDripToken];
    }
  }

  function addDrip(State storage self, address measure, address dripToken, uint256 dripRatePerSecond, uint256 currentTime) internal {
    require(!self.activeBalanceDrips[measure].contains(dripToken), "BalanceDripManager/drip-exists");
    if (self.activeBalanceDrips[measure].count == 0) {
      address[] memory single = new address[](1);
      single[0] = dripToken;
      self.activeBalanceDrips[measure].initialize(single);
    } else {
      self.activeBalanceDrips[measure].addAddress(dripToken);
    }
    self.balanceDrips[measure][dripToken].initialize(currentTime);
    self.balanceDrips[measure][dripToken].dripRatePerSecond = dripRatePerSecond;
  }

  function removeDrip(
    State storage self,
    address measure,
    address prevDripToken,
    address dripToken
  )
    internal
  {
    delete self.balanceDrips[measure][dripToken];
    self.activeBalanceDrips[measure].removeAddress(prevDripToken, dripToken);
  }

  function setDripRate(State storage self, address measure, address dripToken, uint256 dripRatePerSecond) internal {
    require(self.activeBalanceDrips[measure].contains(dripToken), "BalanceDripManager/drip-not-exists");
    self.balanceDrips[measure][dripToken].dripRatePerSecond = dripRatePerSecond;
  }

  function hasDrip(State storage self, address measure, address dripToken) internal view returns (bool) {
    return self.activeBalanceDrips[measure].contains(dripToken);
  }

  function getDrip(State storage self, address measure, address dripToken) internal view returns (BalanceDrip.State storage) {
    return self.balanceDrips[measure][dripToken];
  }

  function balanceOfDrip(
    State storage self,
    address user,
    address measure,
    address dripToken
  )
    internal view
    returns (uint256)
  {
    BalanceDrip.State storage dripState = self.balanceDrips[measure][dripToken];
    return dripState.userStates[user].dripBalance;
  }

  function claimDripTokens(State storage self, address user, address measure, address dripToken) internal returns (uint256) {
    BalanceDrip.State storage dripState = self.balanceDrips[measure][dripToken];
    uint256 balance = dripState.userStates[user].dripBalance;
    dripState.burnDrip(user, balance);
    require(IERC20(dripToken).transfer(user, balance), "BalanceDripManager/transfer-failed");
    return balance;
  }
}

contracts/drip/BalanceDrip.sol.content:
pragma solidity ^0.6.4;

import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/SafeCast.sol";
import "@pooltogether/fixed-point/contracts/FixedPoint.sol";

library BalanceDrip {
  using SafeMath for uint256;
  using SafeCast for uint256;

  struct UserState {
    uint128 lastExchangeRateMantissa;
    uint128 dripBalance;
  }

  struct State {
    uint256 dripRatePerSecond;
    uint128 exchangeRateMantissa;
    uint32 timestamp;
    mapping(address => UserState) userStates;
  }

  function initialize(State storage self, uint256 timestamp) internal {
    self.exchangeRateMantissa = FixedPoint.SCALE.toUint128();
    self.timestamp = timestamp.toUint32();
  }

  function updateExchangeRate(
    State storage self,
    uint256 measureTotalSupply,
    uint256 timestamp
  ) internal {
    // this should only run once per block.
    if (self.timestamp == uint32(timestamp)) {
      return;
    }

    uint256 newSeconds = timestamp.sub(self.timestamp);

    if (newSeconds > 0 && self.dripRatePerSecond > 0) {
      uint256 newTokens = newSeconds.mul(self.dripRatePerSecond);
      uint256 indexDeltaMantissa = measureTotalSupply > 0 ? FixedPoint.calculateMantissa(newTokens, measureTotalSupply) : 0;
      self.exchangeRateMantissa = uint256(self.exchangeRateMantissa).add(indexDeltaMantissa).toUint128();
    }

    self.timestamp = timestamp.toUint32();
  }

  function drip(
    State storage self,
    address user,
    uint256 userMeasureBalance,
    uint256 measureTotalSupply,
    uint256 timestamp
  ) internal returns (uint128) {
    updateExchangeRate(self, measureTotalSupply, timestamp);
    return dripUser(
      self,
      user,
      userMeasureBalance
    );
  }

  function dripUser(
    State storage self,
    address user,
    uint256 userMeasureBalance
  ) internal returns (uint128) {
    UserState storage userState = self.userStates[user];
    uint256 lastExchangeRateMantissa = userState.lastExchangeRateMantissa;
    if (lastExchangeRateMantissa == 0) {
      // if the index is not intialized
      lastExchangeRateMantissa = self.exchangeRateMantissa;
    }

    uint256 deltaExchangeRateMantissa = uint256(self.exchangeRateMantissa).sub(lastExchangeRateMantissa);
    uint256 newTokens = FixedPoint.multiplyUintByMantissa(userMeasureBalance, deltaExchangeRateMantissa);
    uint128 newDripBalance = uint256(userState.dripBalance).add(newTokens).toUint128();
    self.userStates[user] = UserState({
      lastExchangeRateMantissa: self.exchangeRateMantissa,
      dripBalance: newDripBalance
    });

    return newDripBalance;
  }

  function burnDrip(
    State storage self,
    address user,
    uint256 amount
  ) internal {
    UserState storage userState = self.userStates[user];
    userState.dripBalance = uint256(userState.dripBalance).sub(amount).toUint128();
  }
}

contracts/drip/VolumeDrip.sol.content:
pragma solidity ^0.6.4;

import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/utils/SafeCast.sol";
import "@pooltogether/fixed-point/contracts/FixedPoint.sol";

import "../utils/ExtendedSafeCast.sol";

library VolumeDrip {
  using SafeMath for uint256;
  using SafeCast for uint256;
  using ExtendedSafeCast for uint256;

  struct Deposit {
    uint112 balance;
    uint16 period;
    uint128 accrued;
  }

  struct Period {
    uint224 totalSupply;
    uint32 startTime;
  }

  struct State {
    mapping(address => Deposit) deposits;
    mapping(uint32 => Period) periods;
    uint32 periodSeconds;
    uint128 dripAmount;
    uint16 currentPeriodIndex;
  }

  function initialize(
    State storage self,
    uint32 _periodSeconds,
    uint128 dripAmount,
    uint32 startTime
  ) internal {
    require(_periodSeconds > 0, "VolumeDrip/period-gt-zero");
    self.periods[0] = Period({
      totalSupply: 0,
      startTime: startTime
    });
    self.periodSeconds = _periodSeconds;
    self.dripAmount = dripAmount;
  }

  function isPeriodOver(State storage self, uint256 currentTime) internal view returns (bool) {
    return currentTime >= currentPeriodEndAt(self);
  }

  function currentPeriodEndAt(State storage self) internal view returns (uint256) {
    return uint256(self.periods[self.currentPeriodIndex].startTime).add(self.periodSeconds);
  }

  function completePeriod(State storage self, uint256 currentTime) internal onlyPeriodOver(self, currentTime) {
    uint256 lastStartTime = self.periods[self.currentPeriodIndex].startTime;
    uint256 numberOfPeriods = currentTime.sub(lastStartTime).div(self.periodSeconds);
    uint256 startTime = lastStartTime.add(numberOfPeriods.mul(self.periodSeconds));
    self.currentPeriodIndex = uint256(self.currentPeriodIndex).add(1).toUint16();
    self.periods[self.currentPeriodIndex] = Period({
      totalSupply: 0,
      startTime: startTime.toUint32()
    });
  }

  function calculateAccrued(
    State storage self,
    uint16 depositPeriod,
    uint128 balance
  )
    internal view
    returns (uint256)
  {
    uint256 accrued;
    if (depositPeriod < self.currentPeriodIndex && self.periods[depositPeriod].totalSupply > 0) {
      uint256 fractionMantissa = FixedPoint.calculateMantissa(balance, self.periods[depositPeriod].totalSupply);
      accrued = FixedPoint.multiplyUintByMantissa(self.dripAmount, fractionMantissa);
    }


    return accrued;
  }

  function mint(State storage self, address user, uint256 amount, uint256 currentTime) internal onlyPeriodNotOver(self, currentTime) {
    uint256 accrued = calculateAccrued(self, self.deposits[user].period, self.deposits[user].balance);
    uint16 currentPeriod = self.currentPeriodIndex;
    if (accrued > 0) {
      self.deposits[user] = Deposit({
        balance: amount.toUint112(),
        period: currentPeriod,
        accrued: uint256(self.deposits[user].accrued).add(accrued).toUint128()
      });
    } else {
      self.deposits[user] = Deposit({
        balance: uint256(self.deposits[user].balance).add(amount).toUint112(),
        period: currentPeriod,
        accrued: self.deposits[user].accrued
      });
    }
    self.periods[currentPeriod].totalSupply = uint256(self.periods[currentPeriod].totalSupply).add(amount).toUint128();
  }

  function balanceOf(State storage self, address user) internal view returns (Deposit memory) {
    uint256 accrued = calculateAccrued(self, self.deposits[user].period, self.deposits[user].balance);
    uint112 newBalance;
    if (accrued > 0) {
      newBalance = 0;
    } else {
      newBalance = self.deposits[user].balance;
    }
    accrued = accrued.add(self.deposits[user].accrued);
    return Deposit({
      balance: newBalance,
      period: self.currentPeriodIndex,
      accrued: accrued.toUint128()
    });
  }

  function currentPeriod(State storage self) internal view returns (Period memory) {
    return self.periods[self.currentPeriodIndex];
  }

  function burnDrip(State storage self, address user) internal returns (uint256 accrued) {
    Deposit memory deposit = balanceOf(self, user);
    accrued = deposit.accrued;
    deposit.accrued = 0;
    self.deposits[user] = deposit;
    return accrued;
  }

  modifier onlyPeriodNotOver(State storage self, uint256 _currentTime) {
    require(!isPeriodOver(self, _currentTime), "VolumeDrip/period-over");
    _;
  }

  modifier onlyPeriodOver(State storage self, uint256 _currentTime) {
    require(isPeriodOver(self, _currentTime), "VolumeDrip/period-not-over");
    _;
  }
}

contracts/utils/ExtendedSafeCast.sol.content:
pragma solidity ^0.6.4;

library ExtendedSafeCast {

  /**
    * @dev Converts an unsigned uint256 into a unsigned uint112.
    *
    * Requirements:
    *
    * - input must be less than or equal to maxUint112.
    */
  function toUint112(uint256 value) internal pure returns (uint112) {
    require(value < 2**112, "SafeCast: value doesn't fit in an uint112");
    return uint112(value);
  }

  /**
    * @dev Converts an unsigned uint256 into a unsigned uint96.
    *
    * Requirements:
    *
    * - input must be less than or equal to maxUint96.
    */
  function toUint96(uint256 value) internal pure returns (uint96) {
    require(value < 2**96, "SafeCast: value doesn't fit in an uint96");
    return uint96(value);
  }

}

contracts/counterfactual-action/Counterf.content:
pragma solidity 0.6.4;

import "../prize-pool/PrizePool.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";

contract CounterfactualAction {
  function depositTo(address payable user, PrizePool prizePool, address output, bytes calldata data) external {
    IERC20 token = prizePool.token();
    uint256 amount = token.balanceOf(address(this));
    token.approve(address(prizePool), amount);
    prizePool.depositTo(user, amount, output, data);
    selfdestruct(user);
  }

  function cancel(address payable user, PrizePool prizePool) external {
    IERC20 token = prizePool.token();
    token.transfer(user, token.balanceOf(address(this)));
    selfdestruct(user);
  }
}

